mapper.select:
SELECT 	COUNT(*)
FROM 	reservation
WHERE 	(checkIn >= #{checkIn}
AND 	checkIn < #{checkOut})
OR 		(checkOut > #{checkIn}
AND 	checkOut < #{checkOut})
OR 		(checkIn <= #{checkIn}
AND 	checkOut >= #{checkOut})
AND 	guestRoomName = #{guestRoomName}

SELECT 	COUNT(*)
FROM 	guestRoomInfo
WHERE 	guestRoomName = #{guestRoomName}

SELECT 	guestRoomNo
FROM 	reservation
WHERE 	(checkIn >= #{checkIn}
AND 	checkIn < #{checkOut})
OR 		(checkOut > #{checkIn}
AND 	checkOut < #{checkOut})
OR 		(checkIn <= #{checkIn}
AND 	checkOut >= #{checkOut})
AND 	guestRoomName = #{guestRoomName}
